import subprocess
from pathlib import Path

def pull_data():
    model_url = 'https://lab.plantnet.org/seafile/d/01ab6658dad6447c95ae/files/?p=%2Fresnet18_weights_best_acc.tar&dl=1'
    class_mapping_url = 'https://storage.googleapis.com/kagglesdsdata/datasets/1981237/3270629/plantnet_300K/plantnet300K_species_names.json?X-Goog-Algorithm=GOOG4-RSA-SHA256&X-Goog-Credential=gcp-kaggle-com%40kaggle-161607.iam.gserviceaccount.com%2F20250408%2Fauto%2Fstorage%2Fgoog4_request&X-Goog-Date=20250408T152507Z&X-Goog-Expires=259200&X-Goog-SignedHeaders=host&X-Goog-'

    base_dir = Path(__file__).resolve().parent.parent
    models_dir = base_dir / "data/models"
    mapping_dir = base_dir / "data/class_mapping"

    models_dir.mkdir(parents=True, exist_ok=True)
    mapping_dir.mkdir(parents=True, exist_ok=True)

    model_output = models_dir / "resnet18_weights_best_acc.tar"
    class_mapping_output = mapping_dir / "plantnet300K_species_names.json"

    subprocess.run(["wget", "-O", str(model_output), model_url])
    subprocess.run(["wget", "-O", str(class_mapping_output), class_mapping_url])
